public class GrandParent { public void m1(){ System.out.println("GrandParent m1"); } } class Parent extends GrandParent { public void m1(){ System.out.println("Parent m1"); } } class Child extends Parent { public void m1(String msg){ System.out.println("Child m1: " + msg); } } class Tester { public static void main(String[] args) { GrandParent gp = new GrandParent(); Parent p = new Parent(); Child c = new Child(); gp.m1(); p.m1(); c.m1(); c.m1("Hello"); } }